dt-event.example:
var table = $('#example').DataTable( {
	keys: true
} );

table.on( 'key', function ( e, datatable, key, cell, originalEvent ) {
	if ( key === 13 ) { // return
		editor
			.one( 'close', function () {
				table.keys.enable();
			} )
			.inline( cell );

		table.keys.disable();
	}
} );
